typst-template.typ


./_extensions/article/typst-template.typ


// This is an example typst template (based on the default template that ships
// with Quarto). It defines a typst function named 'article' which provides
// various customization options. This function is called from the 
// 'typst-show.typ' file (which maps Pandoc metadata function arguments)
//
// If you are creating or packaging a custom typst template you will likely
// want to replace this file and 'typst-show.typ' entirely. You can find 
// documentation on creating typst templates and some examples here: 
//   - https://typst.app/docs/tutorial/making-a-template/
//   - https://github.com/typst/templates


#let article(
  title: none,
  subtitle: none,
  authors: none,
  date: none,
  abstract: none,
  abstract-title: none,
  cols: 1,
  margin: (x: 1.25in, y: 1.25in),
  paper: "us-letter",
  lang: "en",
  region: "US",
  font: "libertinus serif",
  fontsize: 11pt,
  title-size: 1.5em,
  subtitle-size: 1.25em,
  heading-family: "libertinus serif",
  heading-weight: "bold",
  heading-style: "normal",
  heading-color: black,
  heading-line-height: 0.65em,
  sectionnumbering: none,
  pagenumbering: "1",
  toc: false,
  toc_title: none,
  toc_depth: none,
  toc_indent: 1.5em,
  doc,
) = {
  set page(
    paper: paper,
    margin: margin,
    numbering: pagenumbering,
  )
  set par(justify: true)
  set text(lang: lang,
           region: region,
           font: font,
           size: fontsize)
  set heading(numbering: sectionnumbering)
  if title != none {
    align(center)[#block(inset: 2em)[
      #set par(leading: heading-line-height)
      #if (heading-family != none or heading-weight != "bold" or heading-style != "normal"
           or heading-color != black) {
        set text(font: heading-family, weight: heading-weight, style: heading-style, fill: heading-color)
        text(size: title-size)[#title]
        if subtitle != none {
          parbreak()
          text(size: subtitle-size)[#subtitle]
        }
      } else {
        text(weight: "bold", size: title-size)[#title]
        if subtitle != none {
          parbreak()
          text(weight: "bold", size: subtitle-size)[#subtitle]
        }
      }
    ]]
  }

  if authors != none {
    let count = authors.len()
    let ncols = calc.min(count, 3)
    grid(
      columns: (1fr,) * ncols,
      row-gutter: 1.5em,
      ..authors.map(author =&gt;
          align(center)[
            #author.name \
            #author.affiliation \
            #author.email
          ]
      )
    )
  }

  if date != none {
    align(center)[#block(inset: 1em)[
      #date
    ]]
  }

  if abstract != none {
    block(inset: 2em)[
    #text(weight: "semibold")[#abstract-title] #h(1em) #abstract
    ]
  }

  if toc {
    let title = if toc_title == none {
      auto
    } else {
      toc_title
    }
    block(above: 0em, below: 2em)[
    #outline(
      title: toc_title,
      depth: toc_depth,
      indent: toc_indent
    );
    ]
  }

  if cols == 1 {
    doc
  } else {
    columns(cols, doc)
  }
}

#set table(
  inset: 6pt,
  stroke: none
)

#let whr-template( doc, title: none, author: none, cover_image: none, main_logo: none, bottom_logos: none, toc_background_image: none, ) = { // Page setup for the entire document set text(font: ‚ÄúLinux Libertine‚Äù, size: 10pt) set heading(numbering: ‚Äú1.1‚Äù) set page(paper: ‚Äúa4‚Äù, margin: (left: 20mm, right: 30mm, top: 20mm, bottom: 20mm))
// Define the style for purple headings show heading: it =&gt; { set text(fill: rgb(‚Äú#4B0082‚Äù)) // A deep purple it }
// Define the blue bar on the right side of pages #show page: page =&gt; { // Add the blue bar (except on the cover page) if counter(page).at(1) &gt; 1 { box( width: 10pt, height: 100%, fill: rgb(‚Äú#2a87d6‚Äù), float: true, inset: 0pt, place(right, top) ) } // Render the page content page }
// Cover page layout page( header: none, footer: none, background: cover_image, )[ #place( right, center, image(main_logo) )
#if bottom_logos != none {
  #place(bottom, center, [
    #h(1fr) #bottom_logos #h(1fr)
  ])
}
]
// Table of Contents page page( header: none, footer: none, background: toc_background_image, )[ #show: heading.where(level: 1, 2) #outline(title: ‚ÄúContents‚Äù) ]
// Main content pages #set page(columns: 2) #set page( header: if title != none { box(width: 100%, [ #h(1fr) #text(9pt)[#title] ]) }, footer: box( width: 100%, [#h(1fr) #text(9pt)[#counter(page).at(1)]] ) )
// Insert the main document content #doc }
// Apply the template to the document with the required parameters #show: doc =&gt; whr-template( doc, title: [WORLD HAPPINESS REPORT 2024], cover_image: ‚Äú../assets/cover-page.jpg‚Äù, main_logo: ‚Äú../assets/logo-center-right.png‚Äù, bottom_logos: [ #image(‚Äú../assets/bottom-logo-1.png‚Äù, height: 1.5cm) #h(1cm) #image(‚Äú../assets/bottom-logo-2.png‚Äù, height: 1.5cm) #h(1cm) #image(‚Äú../assets/bottom-logo-3.png‚Äù, height: 1.5cm) ], toc_background_image: ‚Äú../assets/toc-background.jpg‚Äù, )
